import React, { useState, useEffect }from "react";
// import { geoAlbers,geoPath } from 'd3';
import * as d3 from 'd3'
import datum from '../mapfinal.json'
import geojson2svg from 'geojson2svg';
import parseSVG from 'parse-svg';
import useScreenWidth from '../Hooks/useScreenWidth'
import useScreenHeight from '../Hooks/useScreenHeight'
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';

function App() {
  let [mapp,setMapp] = useState([])


  var width = 960,
    height = 1160;

    var projection = d3.geo.albers()
    .center([0, 55.4])
    .rotate([4.4, 0])
    .parallels([50, 60])
    .scale(100)
    .translate([width / 2, height / 2]);
  let path = d3.geo.path().projection(projection)
  let paths = mapp.map(feature => path(feature))
  console.log(mapp)
  return (
    <div className="App">
      <svg fill = "none" width = {width} height = {height} xmlns="http://www.w3.org/2000/svg">
  {
    paths.map((path,index) => index === 5 ? <path stroke='black' fill='red' d = {path}></path> : <path stroke='black' fillRule='nonzero' fill='none' d = {path}></path> )
  }
</svg>
    </div>
  ); 
}

function Marks ({data},props){




