    var projection = geoAlbers()
    .center([0, 55.4])
    .rotate([4.4, 0])
    .parallels([50, 60])
    .scale(1000)
    .translate([width / 2, height / 2]);


const path = geoPath(projection).projection(projection);


return (

    <g className="marks">
      {
        datum.features.filter((feature,index)=> index !== 5).map((feature, index) => {
          // console.log(feature.properties.NAME_2)
          if (index === 12){
