              <path stroke='black' fillRule='nonzero' fill='red' d = {path(feature)}></path> 
            )
          }

          return (

          <path stroke='black' fillRule='nonzero' fill='none' d = {path(feature)}></path> 
        )})
        //svgStrings.map(string => ReactHtmlParser(string))
      }

    </g>
  );
} 

