import React, { useState, useEffect }from "react";
import { geoAlbers,geoPath } from 'd3';

import datum from '../daniel.json'
import geojson2svg from 'geojson2svg';
import parseSVG from 'parse-svg';
import useScreenWidth from '../Hooks/useScreenWidth'
import useScreenHeight from '../Hooks/useScreenHeight'
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';



function Marks ({data},props){




    let screenWidth = useScreenWidth()
    let screenHeight = useScreenHeight()
    var width = 0.3 * screenWidth,
    height = 0.6 * screenHeight;
    var projection = geoAlbers()
    .center([0, 55.4])
    .rotate([4.4, 0])
    .parallels([50, 60])
    .scale(100)
    .translate([width / 2, height / 2]);


const path = geoPath(projection).projection(projection);


return (

    <g className="marks">
      {
        datum.features.map((feature, index) => {
          console.log(feature.properties.Name1)
          if (index === 12){
            console.log(index)
          }

          return (
         <path onClick = {() => console.log('Click event')} fill={index === 9 ? 'none' : 'none'} stroke='black' d = {path(feature)} key = {'yikes'} />
        )})
        //svgStrings.map(string => ReactHtmlParser(string))
      }

    </g>
  );
} 

export default Marks
