import React, { useState, useEffect }from "react";
import { geoAlbers,geoPath } from 'd3';
import geojson2svg from 'geojson2svg';
import useScreenWidth from '../Hooks/useScreenWidth'
import useScreenHeight from '../Hooks/useScreenHeight'



function Marks ({data},props){
    const [fills,setFills] = useState(new Array(data.features.length).fill('none'))
    const [polygons, setPolygons] = useState(0)
    var options =   {
      viewportSize: {width: 200, height: 100},
      mapExtent: {left: -180, bottom: -90, right: 180, top: 90},
      output: 'svg' 
    }
    var converter = geojson2svg(options);
    var svgStrings = converter.convert({data},options);

    let screenWidth = useScreenWidth()
    let screenHeight = useScreenHeight()
    var width = 0.3 * screenWidth,
    height = 0.6 * screenHeight;
    const projection = geoAlbers()
    .center([0, 55.4])
    .rotate([4.4, 0])
    .parallels([50, 60])
    .scale(5000)
    .translate([width / 2, height / 2]);
    let colours = {
      joy: 'red',
      sadness: 'blue',
      anger:'orange',
      fear:'yellow',
      confident:'green'
    }
const path = geoPath(projection).projection(projection);
function changeColour(index){
  	// 1. Make a shallow copy of the array
	let temp_state = [...fills];

	// 2. Make a shallow copy of the element you want to mutate
	let temp_element = fills[index];


	temp_element = 'black'
	// 4. Put it back into our array. N.B. we *are* mutating the array here, but that's why we made a copy first
	temp_state[index] = temp_element;

	// 5. Set the state to our new copy
	setFills( temp_state );
  console.log('done')
}

return (

    <g className="marks">
      {
        data.features.map((feature, index) => {

          return (
         <path onClick = {() => changeColour(index)} fill={fills[index]} stroke='red' d = {path(feature)} key = {feature.properties.NAME_2} />
        )})
      }
    </g>
  );
} 
export default Marks
