import React, { useState, useEffect }from "react";
import { geoAlbers,geoPath } from 'd3';
import useScreenWidth from '../Hooks/useScreenWidth'
import useScreenHeight from '../Hooks/useScreenHeight'



function Marks ({data},props){
    const [fill,setFill] = useState('black')
    let screenWidth = useScreenWidth()
    let screenHeight = useScreenHeight()
    var width = 0.3 ,
    height = 1000;
const projection = geoAlbers()
.center([0, 55.4])
.rotate([4.4, 0])
.parallels([50, 60])
.scale(5000)
.translate([width / 2, height / 2]);
const path = geoPath(projection).projection(projection);

return (

    <g className="marks">
      {

        data.features.map(feature => (
         <path onClick = {() => console.log(feature.properties.NAME_2)} fill='none' stroke='black' d={path(feature)} key= {feature.properties.ENGTYPE_2} />
        ))
      }
    </g>
  );
} 
export default Marks
